import React, { useEffect, useState } from "react";

const Cart = () => {
  const [data, setData] = useState([]);
  const [cardData, setCardData] = useState(null); // Changed to null for a single product

  // Fetch cart data
  const fetchData = async () => {
    try {
      const res = await fetch("https://fakestoreapi.com/carts");
      const cartData = await res.json();
      setData(cartData);
    } catch (e) {
      console.log("Error fetching cart data:", e);
    }
  };

  // Fetch product details based on product ID
  const getData = async (productId) => {
    try {
      const res = await fetch(`https://fakestoreapi.com/products/${productId}`);
      const productData = await res.json();
      setCardData(productData);
    } catch (err) {
      console.log("Error fetching product data:", err);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  useEffect(() => {
    console.log("Cart Data:", data);
    console.log("Product Data:", cardData);
  }, [data, cardData]);

  return (
    <div>
      <h1>Cart List</h1>
      <ul>
        {data.map((cart) => (
          <li key={cart.id}>
            User ID: {cart.userId}
            <ul>
              {cart.products.map((product) => (
                <li key={product.productId}>
                  Product ID: {product.productId} (Quantity: {product.quantity})
                  <button onClick={() => getData(product.productId)}>Details</button>
                </li>
              ))}
            </ul>
          </li>
        ))}
      </ul>
      {cardData && (
        <div>
          <h2>Product Details</h2>
          <p><strong>Title:</strong> {cardData.title}</p>
          <p><strong>Price:</strong> ${cardData.price}</p>
          <p><strong>Description:</strong> {cardData.description}</p>
          <img src={cardData.image} alt={cardData.title} style={{ width: "150px" }} />
        </div>
      )}
    </div>
  );
};

export default Cart;
