import React, { useState } from "react";

const Todo = () => {
  const [input, setInput] = useState("");
  const [list, setList] = useState([]);

  const addTodo = (e) => {
    e.preventDefault();
    if (input) {
      setList([...list, input]);
      setInput("");
    }
    console.log(list);
  };
  const del = (i) => {
    setList(list.filter((item) => item !== i));
    console.log(i);
  };
  return (
    <div>
      <input
        type="text"
        placeholder="enter test"
        onChange={(e) => setInput(e.target.value)}
      />
      <button onClick={addTodo}>Add</button>
      <ul>
        {list.map((t, i) => (
          <li key={i}>
            {t}
            <button onClick={() => del(i)}>Del</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Todo;
