import React, { useEffect, useState } from "react";
import './transfer.css'

const TransferList = () => {
  const [select, setSelect] = useState([]);
  const [transfer, setTransfer] = useState([]);
  const arr = [
    { id: 1, name: "John Doe", amount: 1000 },
    { id: 2, name: "Jane Doe", amount: 500 },
    { id: 3, name: "Michael Doe", amount: 2000 },
  ];

  const handleSelect = (item) => {
    setSelect([...select, item]);
    console.log(select);
  };
  const handleMove = () => {
    setTransfer([...transfer,select]);
    console.log(select);
  };
  useEffect(()=>{
    console.log(transfer)
  },[transfer])
  return (
    <div className="main">
      <div className="transfer-list">
        <h1>Transfer List</h1>
        <div className="transfer-list__header">
          <div className="right_list">
            {arr.map((item, index) => {
              return (
                <div key={index} className="content_div">
                  <div
                    className="list__item__name"
                    onClick={() => handleSelect(item)}
                  >
                    {item.name}
                  </div>
                </div>
              );
            })}
          </div>
          <div>
            <button onClick={handleMove}>Move</button>
          </div>
          <div className="left_list">
            {transfer.map((item, index) => {
              return (
                <div key={index}>
                  <div className="transfer-list__item__name">{item.name}</div>
                </div>
              );
            })}
          </div>
        </div>
      </div>
    </div>
  );
};

export default TransferList;
