import React, { useState } from "react";

const TransferList = () => {
  const [select, setSelect] = useState([]);
  const arr = [
    { id: 1, name: "John Doe", amount: 1000 },
    { id: 2, name: "Jane Doe", amount: 500 },
    { id: 3, name: "Michael Doe", amount: 2000 },
  ];
  const handleMove = (i) => {
    setSelect([...select, arr[i]]);
    console.log(select);
  };
  return (
    <div className="main">
      <div className="transfer-list">
        <h1>Transfer List</h1>
        <div className="transfer-list__header">
          <div className="right_list">
            {arr.map((item, index) => {
              return (
                <div key={index}>
                  <div className="transfer-list__item">
                    <div className="transfer-list__item__name">{item.name}</div>
                    <div className="transfer-list__item__amount">
                      {item.amount}
                    </div>
                  </div>
                </div>
              );
            })}
          </div>
          <div className="left_list"></div>
        </div>
      </div>
    </div>
  );
};

export default TransferList;
