import React, { useEffect, useState } from "react";

const Cart = () => {
  const [data, setData] = useState([]);
  const [cardData, setCardData] = useState([]);
  const fetchData = async () => {
    try {
      const res = await fetch("https://fakestoreapi.com/carts");
      const neData = await res.json();
      setData(neData);
    } catch (e) {
      console.log(e);
    }
  };
  useEffect(() => {
    fetchData();
  }, []);
  useEffect(() => {
    console.log(data);
  }, [data]);
  const getData = async (index) => {
    let key = index;

    try {
      const data = await fetch(` https://fakestoreapi.com/products/${key}`, {
        method: "Post",
        headers: {
          "Content-Type": "application/json",
        },
      });
      const resData = await data.json();
      setCardData(resData);
    } catch (err) {
      console.log(err);
    }
    console.log(index);
  };
  return (
    <div>
      <h1>Cart List</h1>
      <ul>
        {data.map((item, index) => (
          <ul>
            <li key={index}>
              {item.userId}
              <button onClick={() => getData(index)}>Click</button>
            </li>
          </ul>
        ))}
      </ul>
    </div>
  );
};

export default Cart;
